document.addEventListener("DOMContentLoaded", () => {
  const statusMessage = document.getElementById("status-message");
  const dataContainer = document.getElementById("data-container");
  const restartMessage = document.getElementById("restart-message");
  const disconnectedMessage = document.getElementById("disconnected-message");

  const data = %check_removed_user;
  setTimeout(() => {
    dataContainer.querySelector("pre").textContent = JSON.stringify(data,null,2);
    dataContainer.style.display = "block";
    restartMessage.style.display = "block";
    setTimeout(() => {
      restartMessage.style.display = "none";
      dataContainer.style.display = "none";
      disconnectedMessage.style.display = "block";
      setTimeout(() => {
        tk.destroyScene('CHECK_DISCONNECTION v(1.0)');
      }, 3000);
    }, 5000);
  }, 3000);
});

