// Função para gerar os botões de ports de forma dinâmica
function generatePortButtons() {
    const portsContainer = document.getElementById("ports");
    for (let i = 7774; i <= 7799; i++) {
        const button = document.createElement("button");
        button.textContent = `${i}`;
        button.type = "button";
        button.className = "port-btn";
        button.onclick = function () {
            selectPort(i, button);
        };
        portsContainer.appendChild(button);
    }
}

// Função para selecionar a port
function selectPort(port, button) {
    // Remove a classe 'selected' de todos os botões
    const allButtons = document.querySelectorAll("#ports button");
    allButtons.forEach(btn => btn.classList.remove("selected"));

    // Adiciona a classe 'selected' ao botão clicado
    button.classList.add("selected");

    const selectedPort = document.getElementById("selectedPort");
    selectedPort.textContent =`Porta selecionada ${port}`;
document.getElementById("selectedPort");


    // Mostrar a mensagem de sucesso ao selecionar a port
    document.getElementById("portsError").style.display = "none";
}

function validatePhone(input) {
    const phonePattern = /^\d{2} \d{2} \d{8}$/;
    return phonePattern.test(input);
}

function formatphone(input) {
    let value = input.value.replace(/\D/g, ""); // Remove caracteres não numéricos

    if (value.length <= 2) {
        value = value.replace(/(\d{1,2})/, "$1");
    } else if (value.length <= 4) {
        value = value.replace(/(\d{1,2})(\d{1,2})/, "$1 $2");
    } else {
        value = value.replace(/(\d{1,2})(\d{1,2})(\d{1,8})/, "$1 $2 $3");
    }

    input.value = value;

    // Valida o número após formatar
    if (validatePhone(input.value)) {
        console.log("Número válido:", input.value);
    } else {
        console.log("Formato inválido");
    }
}

document.querySelectorAll(".inputTel").forEach(phone => {
    phone.addEventListener("input", function () {
        formatphone(phone);
    });
});

// Função para validar o número de phone
function validatePhone() {
    const phone = document.getElementById("phone").value;
    const phoneError = document.getElementById("phoneError");
    const phoneValid = document.getElementById("phoneValid");

    const phonePattern = /^\d{2}\s\d{2}\s\d{8}$/;
    if (phonePattern.test(phone)) {
        phoneValid.style.display = "block";
        phoneError.style.display = "none";
    } else {
        phoneValid.style.display = "none";
        phoneError.style.display = "block";
    }
}

// Função para validar o formulário
function validateForm(event) {
    event.preventDefault();
    const selectedPort = document.getElementById("selectedPort");

    // Verificar se a port foi selecionada
    if (selectedPort.textContent === "") {
        document.getElementById("portsError").style.display = "block";
    } else {
        document.getElementById("portsError").style.display = "none";

        // Obter dados do formulário
        const phone = document.getElementById("phone").value;
        const modo_op = document.querySelector('input[name="modo_op"]:checked')
            ? document.querySelector('input[name="modo_op"]:checked').value
            : "";
        const salve_media = document.querySelector('input[name="salve_media"]:checked')
            ? document.querySelector('input[name="salve_media"]:checked').value
            : "";
        const auto_del = document.querySelector('input[name="auto_delete"]:checked')
            ? document.querySelector('input[name="auto_delete"]:checked').value
            : "";



const portSelecionada = selectedPort.textContent.replace("Porta selecionada ", "");




        // Criar objeto JSON
        const formData = {
            phone,
            port: portSelecionada,
            modo_op,
            salve_media,
            auto_del
        };

        // Exibir dados no formato JSON no alerta
        //alert(JSON.stringify(formData, null, 2));


      tk.setLocal("json_conect", JSON.stringify(formData, null, 2));

   tk.destroyScene('SCENE_TO_CONNECT_WUZAPI_TO_WHATSAPP_WEB v(1.0)');
    }
}

function lidarEnviar() {
    const inputs = document.querySelectorAll("#deleteNao, #salveNao");
    inputs.forEach(input => input.click());
    document.getElementById("salveSim").disabled = true;
    document.getElementById("deleteSim").disabled = true;
}

function lidarEnviarReceber() {
    const inputs = document.querySelectorAll('input[type="radio"]');
    inputs.forEach(input => (input.checked = false));
    document.getElementById("enviarReceber").checked = true;
    document.getElementById("salveSim").disabled = false;
    document.getElementById("deleteSim").disabled = false;
}

function mostrarHelp() {
    document.getElementById("help-modal").style.display = "grid";
}

function ocultarHelp(event) {
    const modal = document.getElementById("help-modal");
    if (event.target == modal) {
        modal.style.display = "none";
    }
}

// Gerar os botões de ports na inicialização
generatePortButtons();

// Adicionar eventos
document.getElementById("phone").addEventListener("input", validatePhone);
document.getElementById("form").addEventListener("submit", validateForm);
document.getElementById("enviar").addEventListener("click", lidarEnviar);
document
    .getElementById("enviarReceber")
    .addEventListener("click", lidarEnviarReceber);
document.getElementById("help-button").addEventListener("click", mostrarHelp);
document.getElementById("help-modal").addEventListener("click", ocultarHelp);

