var phone = local("phone"); // Variável de entrada %phone
var countryCode = local("cod"); // Variável de entrada countryCode

// Função para formatar corretamente o número de telefone
function formatPhoneNumber(phoneNumber, countryCode) {
    phoneNumber = phoneNumber.replace(/\D/g, ""); // Remove caracteres não numéricos

    if (phoneNumber.length === 10) { // Exemplo: 8197573129
        return `+${countryCode} ${phoneNumber.substring(0, 2)} ${phoneNumber.substring(2, 6)}-${phoneNumber.substring(6)}`;
    } else if (phoneNumber.length === 11) { // Exemplo: 81999412266
        return `+${countryCode} ${phoneNumber.substring(0, 2)} ${phoneNumber.substring(2, 7)}-${phoneNumber.substring(7)}`;
    } else {
        return null;  // Número inválido
    }
}

// Verifica se o código do país foi informado corretamente
if (!countryCode || countryCode.trim().length < 2) {
    setLocal("erro_phone", "Phone_Invalid");  // Define erro se o código do país for inválido
} else {
    var formattedPhone = formatPhoneNumber(phone, countryCode);  // Formatar número

    // Se o número for válido, definir na variável %formatted_phone
    if (formattedPhone) {
        setLocal("formatted_phone", formattedPhone);  // Define o número formatado
        setLocal("err_phone", "Phone_Valid");  // Indica que o telefone é válido
    } else {
        setLocal("err_phone", "Phone_Invalid");  // Define erro se o número for inválido
        setLocal("formatted_phone", "");  // Limpa a variável formatted_phone se o número for inválido
    }
}

var phone = local('formatted_phone');
phone = phone.replace(/[^\d]/g, "");

var waLink = "Iniciar conversa: https://wa.me/" + phone;

setLocal('wa_message_link', waLink);

// Pega as variáveis locais do Tasker
var phone = local('formatted_phone');  // Número de telefone
var message = local('caption_message_link');  // Mensagem

// Remove espaços, "+" e caracteres não numéricos do telefone
phone = phone.replace(/[^\d]/g, "");

// Verifica se a mensagem foi fornecida
if (!message || message.trim() === "") {
    message = "Olá!";  // Mensagem padrão se não houver texto
} else {
    // Codifica a mensagem inteira de forma segura para URL
    message = encodeURIComponent(message);
}

// Gera a URL final
var waLink = "https://wa.me/" + phone + "?text=" + message;

// Define a variável de saída no Tasker
setLocal('wa_message_link', waLink);

var phoneNumbers = local("phone_numbers"); // Recebe a variável de entrada com números separados por vírgula

function validatePhoneNumbers(numbers) {
  // Divide os números da string em um array e remove espaços extras
  var phoneArray = numbers.split(",").map(function(num) {
    return num.trim(); // Remove espaços antes e depois de cada número
  });

  var validNumbers = [];
  var errors = [];

  // Valida cada número
  phoneArray.forEach(function(num, index) {
    if (num.length !== 12) {
      errors.push(`Número incorreto: "${num}" (Índice ${index + 1}) - O número precisa ter 12 dígitos.`);
    } else if (/\s/.test(num)) {
      errors.push(`Número incorreto: "${num}" (Índice ${index + 1}) - O número não pode ter espaços.`);
    } else {
      validNumbers.push(num);
    }
  });

  // Se houver erros, retorna o objeto com erros
  if (errors.length > 0) {
    return {
      "Errors": errors
    };
  }

  // Se não houver erros, cria o JSON com os números válidos
  var result = {
    "Phone": validNumbers
  };

  return result;
}

var formattedData = validatePhoneNumbers(phoneNumbers);

// Formata o JSON com indentação
var formattedJSON = JSON.stringify(formattedData, null, 2);

setLocal("formatted_phone", formattedJSON);

var http_data = local("http_data");

function formatUsersData(responseData) {
  const users = responseData.data.Users;
  var result = [];

  users.forEach(user => {
    var userInfo = {
      "phone": user.Query,
      "IsWhatsapp": user.IsInWhatsapp
    };
    result.push(userInfo);
  });

  return result;
}

var formattedData = formatUsersData(JSON.parse(http_data));

// Formatando o JSON com quebras de linha e indentação
var formattedJSON = JSON.stringify(formattedData, null, 2);

setLocal("inf_users", formattedJSON);

var response = JSON.parse(local("http_data"));

if (response.success) {
    setLocal("profile_url", response.data.url);
    setLocal("profile_id", response.data.id);
    setLocal("profile_type", response.data.type);
    setLocal("profile_path", response.data.direct_path);
} else {
    setLocal("error", "Erro ao obter imagem de perfil.");
}

var httpData = local('http_data');

try {
    var jsonData = JSON.parse(httpData);
    var formattedData = [];

    if (jsonData.code !== 200 || !jsonData.data) {
        throw new Error("Invalid JSON structure");
    }

    var amountContacts = Object.keys(jsonData.data).length;

    for (var contact in jsonData.data) {
        var phoneNumber = contact.split('@')[0];
        var contactInfo = jsonData.data[contact];

        var name = contactInfo.FullName || contactInfo.FirstName || contactInfo.BusinessName || "";
        var isWhatsapp = name ? true : false;

        formattedData.push({
            PhoneNumber: phoneNumber,
            PhoneName: name,
            IsWhatsapp: isWhatsapp,
            FirstName: contactInfo.FirstName || "",
            PushName: contactInfo.PushName || ""
        });
    }

    setLocal("json_contacts", JSON.stringify(formattedData, null, 2));
    setLocal("amount_contacts", amountContacts.toString());
} catch (error) {
    setLocal("json_contacts", JSON.stringify({ error: error.message }, null, 2));
    setLocal("amount_contacts", "0");
}

const response = JSON.parse(http_data);
const groups = response.data.Groups;

const allJIDs = groups.map(group => group.JID.split('@')[0]).join(',');
const allNames = groups.map(group => group.Name).join(',');
const groupInfo = groups.map(group => `${group.Name}|${group.JID.split('@')[0]}`).join(',');

setLocal('%jid_group', allJIDs);
setLocal('%name_group', allNames);
setLocal('%group_info', groupInfo);

var input_audio = local("input_audio"); 
var output_audio = input_audio.replace(/\.(mp3|wav|aac|flac|m4a|wma|ogg|amr|opus)$/i, ".opus");
setLocal("output_audio", output_audio);

var fullPath = local('path');

var fileName = fullPath.split('/').pop().split('.').slice(0, -1).join('.');

setLocal('name_pdf', fileName);

